package Set;

public class Sumofnaturalnumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum=0;
	     int i=1;
	     int num = 100;
	     while(i<=num) 
	     {
	    	 sum = sum+i;
	    	 i++;
	     }
	     System.out.println("sum of natural number is:"+sum);
	}

}
